// P1 - Componente OportunidadeCard (Com Dropdown Hist√≥rico)

import React, { useState } from 'react';
import { OportunidadeData, OPPORTUNITY_COLORS } from '../types/oportunidade';
import { formatters } from '../utils/oportunidadeCalculator';

interface OportunidadeCardProps {
  data: OportunidadeData;
}

const OportunidadeCard: React.FC<OportunidadeCardProps> = ({ data }) => {
  const colors = OPPORTUNITY_COLORS[data.nivel];
  const [historicoOpen, setHistoricoOpen] = useState(false);

  return (
    <div style={{
      backgroundColor: '#FFFFFF',
      borderRadius: '16px',
      overflow: 'hidden',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
      transition: 'all 0.3s ease',
    }}
      onMouseEnter={(e) => {
        e.currentTarget.style.boxShadow = '0 8px 24px rgba(0, 0, 0, 0.15)';
        e.currentTarget.style.transform = 'translateY(-4px)';
      }}
      onMouseLeave={(e) => {
        e.currentTarget.style.boxShadow = '0 4px 12px rgba(0, 0, 0, 0.1)';
        e.currentTarget.style.transform = 'translateY(0)';
      }}
    >
      {/* Header Colorido */}
      <div style={{
        background: colors.gradient,
        padding: '24px 32px',
        color: '#FFFFFF'
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          <div>
            <h3 style={{
              fontSize: '28px',
              fontWeight: '700',
              marginBottom: '4px',
              lineHeight: '1.2'
            }}>
              {data.municipio}
            </h3>
            <p style={{
              fontSize: '14px',
              opacity: 0.9,
              margin: 0
            }}>
              {data.estado} ¬∑ {data.categoria}
            </p>
          </div>
          <div style={{
            padding: '8px 16px',
            borderRadius: '20px',
            backgroundColor: 'rgba(255, 255, 255, 0.2)',
            backdropFilter: 'blur(10px)',
            border: '2px solid rgba(255, 255, 255, 0.3)',
            fontSize: '12px',
            fontWeight: '700',
            textTransform: 'uppercase',
            letterSpacing: '0.5px'
          }}>
            üíé {data.nivel === 'PREMIUM' ? 'Premium' : data.nivel === 'FORTE' ? 'Forte' : 'Boa'}
          </div>
        </div>
      </div>

      {/* Body */}
      <div style={{ padding: '32px' }}>
        {/* M√©trica Principal */}
        <div style={{
          background: `linear-gradient(135deg, ${colors.light} 0%, ${colors.light}ee 100%)`,
          borderRadius: '16px',
          padding: '24px',
          marginBottom: '24px',
          border: `2px solid ${colors.border}`,
          display: 'flex',
          alignItems: 'center',
          gap: '20px'
        }}>
          <div style={{ fontSize: '48px' }}>üí∞</div>
          <div style={{ flex: 1 }}>
            <div style={{
              fontSize: '11px',
              fontWeight: '700',
              color: colors.text,
              textTransform: 'uppercase',
              letterSpacing: '0.5px',
              marginBottom: '8px'
            }}>
              Or√ßamento Dispon√≠vel
            </div>
            <div style={{
              fontSize: '40px',
              fontWeight: '700',
              color: '#1A1A1A',
              lineHeight: '1',
              marginBottom: '8px'
            }}>
              {formatters.currency(data.orcamento.disponivel)}
            </div>
            <div style={{
              fontSize: '14px',
              fontWeight: '600',
              color: colors.text
            }}>
              {data.orcamento.percentualDisponivel.toFixed(1)}% do or√ßamento total
            </div>
          </div>
        </div>

        {/* Grid 3 M√©tricas */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(3, 1fr)',
          gap: '12px',
          marginBottom: '24px'
        }}>
          <MetricBox
            label="Or√ßamento Anual"
            value={formatters.currency(data.orcamento.total)}
            subtitle="M√©dia 2020-24"
          />
          <MetricBox
            label="Executado"
            value={formatters.currency(data.orcamento.executado)}
            subtitle="Jan-Mai 2025"
          />
          <MetricBox
            label="Contratos"
            value={formatters.number(data.contratos.fechados)}
            subtitle="Fechados"
          />
        </div>

        {/* Barra de Execu√ß√£o */}
        <div style={{
          backgroundColor: '#F7F7F7',
          borderRadius: '12px',
          padding: '20px',
          marginBottom: '24px'
        }}>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            marginBottom: '12px'
          }}>
            <span style={{
              fontSize: '14px',
              fontWeight: '700',
              color: '#4B5563'
            }}>
              Execu√ß√£o Or√ßament√°ria 2025
            </span>
            <span style={{
              fontSize: '14px',
              fontWeight: '700',
              color: colors.primary
            }}>
              {data.orcamento.percentualExecutado.toFixed(1)}% executado
            </span>
          </div>

          <div style={{
            height: '24px',
            backgroundColor: '#E5E7EB',
            borderRadius: '12px',
            overflow: 'hidden',
            position: 'relative'
          }}>
            <div style={{
              height: '100%',
              width: `${data.orcamento.percentualExecutado}%`,
              background: colors.gradient,
              borderRadius: '12px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'flex-end',
              paddingRight: '12px',
              transition: 'width 0.7s ease'
            }}>
              <span style={{
                fontSize: '12px',
                fontWeight: '700',
                color: '#FFFFFF'
              }}>
                {data.orcamento.percentualExecutado.toFixed(1)}%
              </span>
            </div>
          </div>

          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            marginTop: '8px',
            fontSize: '12px'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              <div style={{
                width: '8px',
                height: '8px',
                borderRadius: '50%',
                background: colors.primary
              }}></div>
              <span style={{ color: '#6B7280' }}>
                Executado: {formatters.currency(data.orcamento.executado)}
              </span>
            </div>
            <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              <div style={{
                width: '8px',
                height: '8px',
                borderRadius: '50%',
                backgroundColor: '#D1D5DB'
              }}></div>
              <span style={{ color: '#6B7280' }}>
                Dispon√≠vel: {formatters.currency(data.orcamento.disponivel)}
              </span>
            </div>
          </div>
        </div>

        {/* Grid 2x2 Oportunidades */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(2, 1fr)',
          gap: '12px',
          marginBottom: '24px'
        }}>
          <OppBox
            label="Editais Ativos"
            value={formatters.number(data.oportunidades.editaisAtivos)}
            subtitle={`${formatters.currency(data.oportunidades.valorEditais)} em licita√ß√£o`}
          />
          <OppBox
            label="Gap vs M√©dia"
            value={formatters.percent(data.contratos.gapPercentual)}
            subtitle={`${Math.abs(data.contratos.gap)} contratos abaixo`}
          />
          <OppBox
            label="Or√ßamento Livre"
            value={formatters.currency(data.oportunidades.orcamentoLivre)}
            subtitle="Sem edital ainda"
          />
          <OppBox
            label="Proje√ß√£o Ano"
            value={formatters.number(data.contratos.projecaoAno)}
            subtitle={`contratos (vs ${formatters.number(data.contratos.mediaAnual)} m√©dia)`}
          />
        </div>

        {/* Compara√ß√£o Compacta */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(2, 1fr)',
          gap: '12px',
          marginBottom: '24px'
        }}>
          <CompBox
            label="Esperado at√© Maio"
            value={`${formatters.number(data.comparacao.esperadoContratos)} contratos`}
            diff={`Real: ${formatters.number(data.comparacao.realContratos)} (${formatters.percent(data.contratos.gapPercentual)})`}
          />
          <CompBox
            label="Esperado at√© Maio"
            value={formatters.currency(data.comparacao.esperadoValor)}
            diff={`Real: ${formatters.currency(data.comparacao.realValor)} (${formatters.percent(data.oportunidades.diferencaFinanceiraPercent)})`}
          />
        </div>

        {/* Dropdown Hist√≥rico */}
        <div style={{
          backgroundColor: '#F7F7F7',
          borderRadius: '12px',
          border: '2px solid #E5E7EB',
          marginBottom: '24px',
          overflow: 'hidden'
        }}>
          {/* Header do Dropdown */}
          <button
            onClick={() => setHistoricoOpen(!historicoOpen)}
            style={{
              width: '100%',
              padding: '16px 20px',
              backgroundColor: 'transparent',
              border: 'none',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              cursor: 'pointer',
              transition: 'all 0.2s'
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.backgroundColor = '#E5E7EB';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.backgroundColor = 'transparent';
            }}
          >
            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '12px'
            }}>
              <span style={{ fontSize: '20px' }}>üìä</span>
              <span style={{
                fontSize: '14px',
                fontWeight: '700',
                color: '#1A1A1A'
              }}>
                Hist√≥rico 2020-2024
              </span>
              <span style={{
                fontSize: '12px',
                fontWeight: '600',
                color: '#6B7280',
                backgroundColor: '#E5E7EB',
                padding: '4px 12px',
                borderRadius: '12px'
              }}>
                {data.historico.length} anos
              </span>
            </div>
            <div style={{
              fontSize: '20px',
              color: '#6B7280',
              transform: historicoOpen ? 'rotate(180deg)' : 'rotate(0deg)',
              transition: 'transform 0.3s ease'
            }}>
              ‚ñº
            </div>
          </button>

          {/* Conte√∫do do Dropdown */}
          <div style={{
            maxHeight: historicoOpen ? '500px' : '0',
            transition: 'max-height 0.4s ease-in-out',
            overflow: 'hidden'
          }}>
            <div style={{ padding: '0 20px 20px' }}>
              <table style={{
                width: '100%',
                borderCollapse: 'collapse'
              }}>
                <thead>
                  <tr style={{
                    borderBottom: '2px solid #D1D5DB'
                  }}>
                    <th style={{
                      textAlign: 'left',
                      padding: '12px 8px',
                      fontSize: '11px',
                      fontWeight: '700',
                      color: '#6B7280',
                      textTransform: 'uppercase',
                      letterSpacing: '0.5px'
                    }}>
                      Ano
                    </th>
                    <th style={{
                      textAlign: 'right',
                      padding: '12px 8px',
                      fontSize: '11px',
                      fontWeight: '700',
                      color: '#6B7280',
                      textTransform: 'uppercase',
                      letterSpacing: '0.5px'
                    }}>
                      Contratos
                    </th>
                    <th style={{
                      textAlign: 'right',
                      padding: '12px 8px',
                      fontSize: '11px',
                      fontWeight: '700',
                      color: '#6B7280',
                      textTransform: 'uppercase',
                      letterSpacing: '0.5px'
                    }}>
                      Executado
                    </th>
                    <th style={{
                      textAlign: 'right',
                      padding: '12px 8px',
                      fontSize: '11px',
                      fontWeight: '700',
                      color: '#6B7280',
                      textTransform: 'uppercase',
                      letterSpacing: '0.5px'
                    }}>
                      Ticket M√©dio
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {data.historico.map((ano, index) => (
                    <tr
                      key={ano.ano}
                      style={{
                        borderBottom: '1px solid #E5E7EB',
                        transition: 'background-color 0.2s'
                      }}
                      onMouseEnter={(e) => {
                        e.currentTarget.style.backgroundColor = '#F9FAFB';
                      }}
                      onMouseLeave={(e) => {
                        e.currentTarget.style.backgroundColor = 'transparent';
                      }}
                    >
                      <td style={{
                        padding: '12px 8px',
                        fontSize: '14px',
                        fontWeight: '700',
                        color: '#1A1A1A'
                      }}>
                        {ano.ano}
                      </td>
                      <td style={{
                        padding: '12px 8px',
                        fontSize: '14px',
                        fontWeight: '600',
                        color: '#1A1A1A',
                        textAlign: 'right'
                      }}>
                        {formatters.number(ano.contratos)}
                      </td>
                      <td style={{
                        padding: '12px 8px',
                        fontSize: '14px',
                        fontWeight: '600',
                        color: '#1A1A1A',
                        textAlign: 'right'
                      }}>
                        {formatters.currency(ano.valorExecutado)}
                      </td>
                      <td style={{
                        padding: '12px 8px',
                        fontSize: '14px',
                        fontWeight: '600',
                        color: '#1A1A1A',
                        textAlign: 'right'
                      }}>
                        {formatters.currency(ano.ticketMedio)}
                      </td>
                    </tr>
                  ))}
                  {/* Linha de M√©dia */}
                  <tr style={{
                    backgroundColor: colors.light,
                    borderTop: '2px solid ' + colors.border
                  }}>
                    <td style={{
                      padding: '12px 8px',
                      fontSize: '14px',
                      fontWeight: '700',
                      color: colors.text
                    }}>
                      M√©dia
                    </td>
                    <td style={{
                      padding: '12px 8px',
                      fontSize: '14px',
                      fontWeight: '700',
                      color: colors.text,
                      textAlign: 'right'
                    }}>
                      {formatters.number(data.contratos.mediaAnual)}
                    </td>
                    <td style={{
                      padding: '12px 8px',
                      fontSize: '14px',
                      fontWeight: '700',
                      color: colors.text,
                      textAlign: 'right'
                    }}>
                      {formatters.currency(data.orcamento.total)}
                    </td>
                    <td style={{
                      padding: '12px 8px',
                      fontSize: '14px',
                      fontWeight: '700',
