import React from 'react';
import { ComparisonMode } from '../types/municipality';

interface AdvancedFiltersProps {
  searchTerm: string;
  onSearchChange: (value: string) => void;
  comparisonMode: ComparisonMode;
  onComparisonModeChange: (mode: ComparisonMode) => void;
  selectedRegion: string;
  onRegionChange: (region: string) => void;
  sortBy: 'deviation' | 'extra' | 'executed';
  onSortByChange: (sort: 'deviation' | 'extra' | 'executed') => void;
  chartType: 'line' | 'bar';
  onChartTypeChange: (type: 'line' | 'bar') => void;
}

const AdvancedFilters: React.FC<AdvancedFiltersProps> = ({
  searchTerm,
  onSearchChange,
  comparisonMode,
  onComparisonModeChange,
  selectedRegion,
  onRegionChange,
  sortBy,
  onSortByChange,
  chartType,
  onChartTypeChange
}) => {
  const regions = ['Todos', 'Norte', 'Nordeste', 'Centro-Oeste', 'Sudeste', 'Sul'];

  const comparisonModes = [
    { value: 'avg-electoral', label: 'üìä M√©dia Eleitorais', icon: 'üìä' },
    { value: 'last-electoral', label: 'üó≥Ô∏è √öltimo Eleitoral', icon: 'üó≥Ô∏è' },
    { value: 'avg-all', label: 'üìà M√©dia Geral', icon: 'üìà' },
    { value: 'previous-year', label: 'üìÖ Ano Anterior', icon: 'üìÖ' }
  ];

  const sortOptions = [
    { value: 'deviation', label: 'Maior Desvio' },
    { value: 'extra', label: 'Maior Sobra' },
    { value: 'executed', label: 'Maior Execu√ß√£o' }
  ];

  return (
    <div style={{
      backgroundColor: '#ffffff',
      borderRadius: '12px',
      padding: '24px',
      boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)',
      border: '1px solid #f5edff',
      marginBottom: '24px'
    }}>
      <h3 style={{
        fontSize: '16px',
        fontWeight: '700',
        color: '#2e3138',
        marginBottom: '20px',
        display: 'flex',
        alignItems: 'center',
        gap: '8px'
      }}>
        üîç Filtros Avan√ßados
      </h3>

      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
        gap: '16px'
      }}>
        {/* Busca */}
        <div>
          <label style={{
            display: 'block',
            fontSize: '12px',
            fontWeight: '600',
            color: '#5219a1',
            marginBottom: '8px',
            textTransform: 'uppercase'
          }}>
            Buscar Munic√≠pio
          </label>
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => onSearchChange(e.target.value)}
            placeholder="Digite o nome..."
            style={{
              width: '100%',
              padding: '10px 14px',
              fontSize: '14px',
              border: '2px solid #e1cdfe',
              borderRadius: '8px',
              backgroundColor: '#f9f4ff',
              color: '#2e3138',
              outline: 'none',
              transition: 'all 0.2s'
            }}
            onFocus={(e) => {
              e.currentTarget.style.borderColor = '#9755fe';
              e.currentTarget.style.backgroundColor = '#ffffff';
            }}
            onBlur={(e) => {
              e.currentTarget.style.borderColor = '#e1cdfe';
              e.currentTarget.style.backgroundColor = '#f9f4ff';
            }}
          />
        </div>

        {/* Modo de Compara√ß√£o */}
        <div>
          <label style={{
            display: 'block',
            fontSize: '12px',
            fontWeight: '600',
            color: '#5219a1',
            marginBottom: '8px',
            textTransform: 'uppercase'
          }}>
            Comparar com
          </label>
          <select
            value={comparisonMode}
            onChange={(e) => onComparisonModeChange(e.target.value as ComparisonMode)}
            style={{
              width: '100%',
              padding: '10px 14px',
              fontSize: '14px',
              border: '2px solid #e1cdfe',
              borderRadius: '8px',
              backgroundColor: '#f9f4ff',
              color: '#2e3138',
              cursor: 'pointer',
              outline: 'none'
            }}
          >
            {comparisonModes.map(mode => (
              <option key={mode.value} value={mode.value}>
                {mode.label}
              </option>
            ))}
          </select>
        </div>

        {/* Regi√£o */}
        <div>
          <label style={{
            display: 'block',
            fontSize: '12px',
            fontWeight: '600',
            color: '#5219a1',
            marginBottom: '8px',
            textTransform: 'uppercase'
          }}>
            Regi√£o
          </label>
          <select
            value={selectedRegion}
            onChange={(e) => onRegionChange(e.target.value)}
            style={{
              width: '100%',
              padding: '10px 14px',
              fontSize: '14px',
              border: '2px solid #e1cdfe',
              borderRadius: '8px',
              backgroundColor: '#f9f4ff',
              color: '#2e3138',
              cursor: 'pointer',
              outline: 'none'
            }}
          >
            {regions.map(region => (
              <option key={region} value={region}>
                {region}
              </option>
            ))}
          </select>
        </div>

        {/* Ordena√ß√£o */}
        <div>
          <label style={{
            display: 'block',
            fontSize: '12px',
            fontWeight: '600',
            color: '#5219a1',
            marginBottom: '8px',
            textTransform: 'uppercase'
          }}>
            Ordenar por
          </label>
          <select
            value={sortBy}
            onChange={(e) => onSortByChange(e.target.value as any)}
            style={{
              width: '100%',
              padding: '10px 14px',
              fontSize: '14px',
              border: '2px solid #e1cdfe',
              borderRadius: '8px',
              backgroundColor: '#f9f4ff',
              color: '#2e3138',
              cursor: 'pointer',
              outline: 'none'
            }}
          >
            {sortOptions.map(option => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </div>

        {/* Tipo de Gr√°fico */}
        <div>
          <label style={{
            display: 'block',
            fontSize: '12px',
            fontWeight: '600',
            color: '#5219a1',
            marginBottom: '8px',
            textTransform: 'uppercase'
          }}>
            Tipo de Gr√°fico
          </label>
          <div style={{
            display: 'flex',
            gap: '8px'
          }}>
            <button
              onClick={() => onChartTypeChange('line')}
              style={{
                flex: 1,
                padding: '10px',
                fontSize: '14px',
                fontWeight: '600',
                border: '2px solid',
                borderColor: chartType === 'line' ? '#9755fe' : '#e1cdfe',
                borderRadius: '8px',
                backgroundColor: chartType === 'line' ? '#f9f4ff' : '#ffffff',
                color: chartType === 'line' ? '#9755fe' : '#6b7280',
                cursor: 'pointer',
                transition: 'all 0.2s'
              }}
            >
              üìà
            </button>
            <button
              onClick={() => onChartTypeChange('bar')}
              style={{
                flex: 1,
                padding: '10px',
                fontSize: '14px',
                fontWeight: '600',
                border: '2px solid',
                borderColor: chartType === 'bar' ? '#9755fe' : '#e1cdfe',
                borderRadius: '8px',
                backgroundColor: chartType === 'bar' ? '#f9f4ff' : '#ffffff',
                color: chartType === 'bar' ? '#9755fe' : '#6b7280',
                cursor: 'pointer',
                transition: 'all 0.2s'
              }}
            >
              üìä
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdvancedFilters;
