import React, { useMemo } from 'react';
import AnomalyHeroStats from './components/AnomalyHeroStats';
import AnomalyTimeline from './components/AnomalyTimeline';
import AnomalyCard from './components/AnomalyCard';
import { enrichedMockData } from './mockDataEnriched';
import { detectYearlyAnomalies, getTopAnomalies, calculateAnomalyStats } from './utils/anomalyDetection';

function AnomalyDashboard() {
  // Agrupa dados por ano
  const yearlyData = useMemo(() => {
    const grouped: Record<number, { total: number; count: number; isElection: boolean }> = {};

    enrichedMockData.forEach(item => {
      if (!grouped[item.year]) {
        grouped[item.year] = { total: 0, count: 0, isElection: item.isElectionYear };
      }
      grouped[item.year].total += item.executed;
      grouped[item.year].count++;
    });

    return Object.keys(grouped).map(year => ({
      year: parseInt(year),
      value: grouped[parseInt(year)].total,
      isElectionYear: grouped[parseInt(year)].isElection
    }));
  }, []);

  // Detecta anomalias
  const anomalies = useMemo(() => {
    return detectYearlyAnomalies(yearlyData);
  }, [yearlyData]);

  // Top 3 anomalias
  const topAnomalies = useMemo(() => {
    return getTopAnomalies(anomalies, 3);
  }, [anomalies]);

  // Estat√≠sticas gerais
  const stats = useMemo(() => {
    return calculateAnomalyStats(anomalies);
  }, [anomalies]);

  // Insights autom√°ticos
  const insights = useMemo(() => {
    const insights: string[] = [];

    if (stats.electionPattern > 5) {
      insights.push(`Anos eleitorais apresentam aumento m√©dio de ${stats.electionPattern.toFixed(1)}% no or√ßamento`);
    } else if (stats.electionPattern < -5) {
      insights.push(`Anos eleitorais t√™m redu√ß√£o m√©dia de ${Math.abs(stats.electionPattern).toFixed(1)}% no or√ßamento`);
    }

    const largestDrop = topAnomalies.find(a => a.zScore < 0);
    if (largestDrop) {
      insights.push(`Maior corte hist√≥rico: ${largestDrop.year} (${largestDrop.percentageDeviation.toFixed(1)}%)`);
    }

    const pandemicYears = anomalies.filter(a =>
      (a.year === 2020 || a.year === 2021) && a.zScore < 0
    );
    if (pandemicYears.length > 0) {
      const avgPandemicImpact = pandemicYears.reduce((sum, a) => sum + a.percentageDeviation, 0) / pandemicYears.length;
      insights.push(`Pandemia impactou ${Math.abs(avgPandemicImpact).toFixed(1)}% do or√ßamento (2020-2021)`);
    }

    if (stats.criticalCount > 0) {
      insights.push(`${stats.criticalCount} ano(s) com anomalia cr√≠tica (>3œÉ) detectada`);
    }

    const cyclicPattern = anomalies.filter(a => a.isElectionYear && a.zScore > 0).length;
    if (cyclicPattern >= 3) {
      insights.push(`Padr√£o c√≠clico detectado: aumentos consistentes em anos eleitorais`);
    }

    return insights;
  }, [anomalies, topAnomalies, stats]);

  return (
    <div style={{
      minHeight: '100vh',
      backgroundColor: '#f9f4ff',
      padding: '32px',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif'
    }}>
      {/* Hero Stats */}
      <AnomalyHeroStats
        totalAnomalies={stats.totalAnomalies}
        criticalCount={stats.criticalCount}
        moderateCount={stats.moderateCount}
        electionPattern={stats.electionPattern}
        maxAnomaly={stats.maxAnomaly}
      />

      {/* Timeline */}
      <div style={{ marginBottom: '32px' }}>
        <AnomalyTimeline anomalies={anomalies} />
      </div>

      {/* Insights Autom√°ticos */}
      <div style={{
        backgroundColor: '#ffffff',
        borderRadius: '12px',
        padding: '24px',
        marginBottom: '32px',
        boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)',
        border: '1px solid #f5edff'
      }}>
        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '12px',
          marginBottom: '16px'
        }}>
          <span style={{ fontSize: '24px' }}>üí°</span>
          <h3 style={{
            fontSize: '18px',
            fontWeight: '600',
            color: '#2e3138',
            margin: 0
          }}>
            Insights Principais
          </h3>
        </div>
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          gap: '12px'
        }}>
          {insights.map((insight, index) => (
            <div
              key={index}
              style={{
                padding: '12px 16px',
                backgroundColor: '#f9f4ff',
                borderRadius: '8px',
                borderLeft: '4px solid #9755fe',
                fontSize: '14px',
                color: '#2e3138',
                lineHeight: '1.6'
              }}
            >
              <strong>{index + 1}.</strong> {insight}
            </div>
          ))}
        </div>
      </div>

      {/* Top 3 Anomalias */}
      <div style={{ marginBottom: '32px' }}>
        <h2 style={{
          fontSize: '24px',
          fontWeight: '700',
          color: '#2e3138',
          marginBottom: '20px'
        }}>
          üéØ Maiores Anomalias Detectadas
        </h2>
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))',
          gap: '24px'
        }}>
          {topAnomalies.map((anomaly, index) => (
            <AnomalyCard
              key={anomaly.year}
              anomaly={anomaly}
              rank={index + 1}
            />
          ))}
        </div>
      </div>

      {/* Footer com explica√ß√£o metodol√≥gica */}
      <div style={{
        marginTop: '48px',
        padding: '24px',
        backgroundColor: '#ffffff',
        borderRadius: '12px',
        border: '1px solid #e1cdfe'
      }}>
        <h4 style={{
          fontSize: '16px',
          fontWeight: '600',
          color: '#3f127d',
          marginBottom: '12px'
        }}>
          üìö Metodologia de An√°lise
        </h4>
        <div style={{
          fontSize: '13px',
          color: '#6b7280',
          lineHeight: '1.8'
        }}>
          <p style={{ margin: '0 0 8px 0' }}>
            <strong>Detec√ß√£o de Anomalias:</strong> Utilizamos an√°lise estat√≠stica baseada em Z-score (desvio padr√£o)
            para identificar anos com comportamento or√ßament√°rio significativamente diferente da m√©dia hist√≥rica.
          </p>
          <p style={{ margin: '0 0 8px 0' }}>
            <strong>Classifica√ß√£o:</strong> üî¥ Cr√≠tico (&gt;3œÉ), üü° Moderado (2-3œÉ), üü¢ Normal (&lt;2œÉ)
          </p>
          <p style={{ margin: '0' }}>
            <strong>Contexto:</strong> An√°lise considera eventos pol√≠ticos (elei√ß√µes, mudan√ßas de governo),
            econ√¥micos (crises) e sanit√°rios (pandemia) para interpreta√ß√£o dos desvios.
          </p>
        </div>
      </div>

      {/* Footer */}
      <div style={{
        marginTop: '32px',
        padding: '16px',
        textAlign: 'center',
        color: '#6b7280',
        fontSize: '13px'
      }}>
        <p style={{ margin: 0 }}>
          Dashboard de An√°lise Estat√≠stica de Anomalias Or√ßament√°rias ¬∑ Educa√ß√£o Brasil (2015-2024)
        </p>
        <p style={{ margin: '8px 0 0 0', fontSize: '12px' }}>
          Powered by Claude Code ¬∑ An√°lise autom√°tica baseada em desvio padr√£o
        </p>
      </div>
    </div>
  );
}

export default AnomalyDashboard;
