import React, { useState, useMemo } from 'react';
import { municipalityBudgetData } from './mockData/municipalityData';
import { ComparisonMode } from './types/municipality';
import MunicipalityDetailCard from './components/MunicipalityDetailCard';
import AdvancedFilters from './components/AdvancedFilters';
import MunicipalityCard from './components/MunicipalityCard';
import MunicipalityComparisonChart from './components/MunicipalityComparisonChart';
import { formatCurrency } from './utils';
import { COPY, formatCopyTemplate } from './constants/copy';

function StrategicDashboard() {
  const [comparisonMode, setComparisonMode] = useState<ComparisonMode>('avg-electoral');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedRegion, setSelectedRegion] = useState('Todos');
  const [sortBy, setSortBy] = useState<'deviation' | 'extra' | 'executed'>('extra');
  const [chartType, setChartType] = useState<'line' | 'bar'>('line');
  const [currentPeriod, setCurrentPeriod] = useState<number>(2024);
  const [comparisonPeriod, setComparisonPeriod] = useState<number>(2020);
  const [comparisonMetric, setComparisonMetric] = useState<'executed' | 'deviation' | 'extra'>('extra');
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 12;

  // Filtragem e ordena칞칚o
  const filteredAndSortedMunicipalities = useMemo(() => {
    let filtered = municipalityBudgetData;

    // Filtro de busca
    if (searchTerm) {
      filtered = filtered.filter(m =>
        m.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        m.state.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Filtro de regi칚o
    if (selectedRegion !== 'Todos') {
      filtered = filtered.filter(m => m.region === selectedRegion);
    }

    // Ordena칞칚o
    return [...filtered].sort((a, b) => {
      const getComparison = (m: typeof municipalityBudgetData[0]) => {
        switch (comparisonMode) {
          case 'avg-electoral': return m.comparison.avgElectoral;
          case 'last-electoral': return m.comparison.lastElectoral;
          case 'avg-all': return m.comparison.avgAll;
          case 'previous-year': return m.comparison.previousYear;
        }
      };

      const compA = getComparison(a);
      const compB = getComparison(b);

      if (sortBy === 'deviation') {
        return compA.percentageDeviation - compB.percentageDeviation;
      } else if (sortBy === 'extra') {
        const extraA = compA.referenceAmount - compA.executedAmount;
        const extraB = compB.referenceAmount - compB.executedAmount;
        return extraB - extraA;
      } else {
        return compB.executedAmount - compA.executedAmount;
      }
    });
  }, [searchTerm, selectedRegion, sortBy, comparisonMode]);

  // Top 3 para destaque
  const top3 = filteredAndSortedMunicipalities.slice(0, 3);

  // Dados de compara칞칚o para o gr치fico (Top 10)
  const comparisonData = useMemo(() => {
    const getComparison = (m: typeof municipalityBudgetData[0]) => {
      switch (comparisonMode) {
        case 'avg-electoral': return m.comparison.avgElectoral;
        case 'last-electoral': return m.comparison.lastElectoral;
        case 'avg-all': return m.comparison.avgAll;
        case 'previous-year': return m.comparison.previousYear;
      }
    };

    return filteredAndSortedMunicipalities.slice(0, 10).map(m => {
      const comp = getComparison(m);

      // Pegar o valor executado do per칤odo de compara칞칚o dos dados hist칩ricos
      const comparisonYearData = m.history.find(h => h.year === comparisonPeriod);

      return {
        name: m.name,
        executed: comp.executedAmount,
        reference: comp.referenceAmount,
        deviation: comp.percentageDeviation,
        comparisonExecuted: comparisonYearData ? comparisonYearData.executed : undefined
      };
    });
  }, [filteredAndSortedMunicipalities, comparisonMode, comparisonPeriod]);

  // Pagina칞칚o
  const totalPages = Math.ceil(filteredAndSortedMunicipalities.length / itemsPerPage);
  const paginatedMunicipalities = filteredAndSortedMunicipalities.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  // Estat칤sticas gerais detalhadas
  const stats = useMemo(() => {
    const getComparison = (mun: typeof filteredAndSortedMunicipalities[0]) => {
      switch (comparisonMode) {
        case 'avg-electoral': return mun.comparison.avgElectoral;
        case 'last-electoral': return mun.comparison.lastElectoral;
        case 'avg-all': return mun.comparison.avgAll;
        case 'previous-year': return mun.comparison.previousYear;
      }
    };

    const totalExtra = filteredAndSortedMunicipalities.reduce((sum, m) => {
      const comp = getComparison(m);
      return sum + (comp.referenceAmount - comp.executedAmount);
    }, 0);

    const totalBudget = filteredAndSortedMunicipalities.reduce((sum, m) => sum + m.totalBudget, 0);
    const totalExecuted = filteredAndSortedMunicipalities.reduce((sum, m) => sum + m.executed, 0);

    const avgDeviation = filteredAndSortedMunicipalities.reduce((sum, m) => {
      return sum + Math.abs(getComparison(m).percentageDeviation);
    }, 0) / (filteredAndSortedMunicipalities.length || 1);

    // Contar por severidade
    const critical = filteredAndSortedMunicipalities.filter(m => getComparison(m).severity === 'critical').length;
    const high = filteredAndSortedMunicipalities.filter(m => getComparison(m).severity === 'high').length;

    // Ranking por estado
    const byState = filteredAndSortedMunicipalities.reduce((acc, m) => {
      if (!acc[m.state]) {
        acc[m.state] = { count: 0, totalExtra: 0 };
      }
      acc[m.state].count++;
      acc[m.state].totalExtra += (getComparison(m).referenceAmount - getComparison(m).executedAmount);
      return acc;
    }, {} as Record<string, { count: number; totalExtra: number }>);

    const stateRanking = Object.entries(byState)
      .map(([state, data]) => ({
        state,
        count: data.count,
        totalExtra: data.totalExtra
      }))
      .sort((a, b) => b.totalExtra - a.totalExtra)
      .slice(0, 5);

    return {
      totalExtra,
      totalBudget,
      totalExecuted,
      avgDeviation,
      critical,
      high,
      stateRanking,
      executionRate: (totalExecuted / totalBudget) * 100
    };
  }, [filteredAndSortedMunicipalities, comparisonMode]);

  return (
    <div style={{
      minHeight: '100vh',
      backgroundColor: '#f9f4ff',
      padding: '32px',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif'
    }}>
      {/* Hero Section - Resumo Brasil */}
      <div style={{
        backgroundColor: '#2e3138',
        borderRadius: '16px',
        padding: '40px',
        marginBottom: '32px',
        boxShadow: '0 4px 16px rgba(0, 0, 0, 0.15)',
        background: 'linear-gradient(135deg, #2e3138 0%, #3f127d 100%)'
      }}>
        <div style={{ textAlign: 'center', marginBottom: '24px' }}>
          <div style={{
            fontSize: '14px',
            fontWeight: '600',
            color: '#d1b3ff',
            textTransform: 'uppercase',
            letterSpacing: '1px',
            marginBottom: '8px'
          }}>
            {COPY.header.eyebrow}
          </div>
          <h1 style={{
            fontSize: '36px',
            fontWeight: '700',
            color: '#ffffff',
            margin: 0,
            lineHeight: '1.2'
          }}>
            {COPY.header.title}
          </h1>
          <p style={{
            fontSize: '15px',
            color: '#ba90fc',
            marginTop: '12px',
            margin: 0
          }}>
            {COPY.header.subtitle}
          </p>
        </div>

        {/* Stats Grid - Principal */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(240px, 1fr))',
          gap: '16px',
          marginBottom: '16px'
        }}>
          <div style={{
            backgroundColor: 'rgba(16, 185, 129, 0.15)',
            borderRadius: '12px',
            padding: '20px',
            border: '2px solid rgba(16, 185, 129, 0.3)',
            backdropFilter: 'blur(10px)'
          }}>
            <div style={{
              fontSize: '11px',
              fontWeight: '700',
              color: '#6ee7b7',
              marginBottom: '8px',
              textTransform: 'uppercase',
              letterSpacing: '0.5px'
            }}>
              {COPY.metrics.totalDisponivel.label}
            </div>
            <div style={{
              fontSize: '36px',
              fontWeight: '700',
              color: '#ffffff',
              lineHeight: '1',
              marginBottom: '8px'
            }}>
              {formatCurrency(stats.totalExtra, true)}
            </div>
            <div style={{
              fontSize: '12px',
              color: '#a7f3d0',
              marginBottom: '4px'
            }}>
              {formatCopyTemplate(COPY.metrics.totalDisponivel.labelSecondary, {
                total: formatCurrency(stats.totalBudget, true)
              })}
            </div>
            <div style={{
              fontSize: '12px',
              color: '#6ee7b7',
              fontWeight: '600',
              display: 'flex',
              alignItems: 'center',
              gap: '6px'
            }}>
              <span>游늳</span>
              <span>{formatCopyTemplate(COPY.metrics.totalDisponivel.extra, {
                valor: formatCurrency(stats.totalExtra * 0.4, true)
              })}</span>
            </div>
          </div>

          <div style={{
            backgroundColor: 'rgba(59, 130, 246, 0.15)',
            borderRadius: '12px',
            padding: '20px',
            border: '2px solid rgba(59, 130, 246, 0.3)',
            backdropFilter: 'blur(10px)'
          }}>
            <div style={{
              fontSize: '11px',
              fontWeight: '700',
              color: '#93c5fd',
              marginBottom: '8px',
              textTransform: 'uppercase',
              letterSpacing: '0.5px'
            }}>
              {COPY.metrics.taxaExecucao.label}
            </div>
            <div style={{
              fontSize: '36px',
              fontWeight: '700',
              color: '#ffffff',
              lineHeight: '1',
              marginBottom: '8px'
            }}>
              {stats.executionRate.toFixed(1)}% executado
            </div>
            <div style={{
              fontSize: '12px',
              color: '#bfdbfe',
              marginBottom: '4px'
            }}>
              {formatCopyTemplate(COPY.metrics.taxaExecucao.labelSecondary, {
                executado: formatCurrency(stats.totalExecuted, true),
                total: formatCurrency(stats.totalBudget, true)
              })}
            </div>
            <div style={{
              fontSize: '11px',
              color: '#93c5fd',
              marginBottom: '4px'
            }}>
              {formatCopyTemplate(COPY.metrics.taxaExecucao.context, {
                media: '85'
              })}
            </div>
            <div style={{
              fontSize: '12px',
              color: '#fca5a5',
              fontWeight: '600',
              display: 'flex',
              alignItems: 'center',
              gap: '6px'
            }}>
              <span>{COPY.metrics.taxaExecucao.alert}</span>
            </div>
          </div>

          <div style={{
            backgroundColor: 'rgba(239, 68, 68, 0.15)',
            borderRadius: '12px',
            padding: '20px',
            border: '2px solid rgba(239, 68, 68, 0.3)',
            backdropFilter: 'blur(10px)'
          }}>
            <div style={{
              fontSize: '11px',
              fontWeight: '700',
              color: '#fca5a5',
              marginBottom: '8px',
              textTransform: 'uppercase',
              letterSpacing: '0.5px'
            }}>
              游뚿 Desvio do Padr칚o
            </div>
            <div style={{
              fontSize: '36px',
              fontWeight: '700',
              color: '#ffffff',
              lineHeight: '1',
              marginBottom: '8px'
            }}>
              {stats.avgDeviation.toFixed(1)}%
            </div>
            <div style={{
              fontSize: '12px',
              color: '#fecaca',
              display: 'flex',
              alignItems: 'center',
              gap: '6px'
            }}>
              <span>丘멆잺</span>
              <span>{stats.critical} cr칤ticos, {stats.high} altos</span>
            </div>
          </div>

          <div style={{
            backgroundColor: 'rgba(168, 85, 247, 0.15)',
            borderRadius: '12px',
            padding: '20px',
            border: '2px solid rgba(168, 85, 247, 0.3)',
            backdropFilter: 'blur(10px)'
          }}>
            <div style={{
              fontSize: '11px',
              fontWeight: '700',
              color: '#d8b4fe',
              marginBottom: '8px',
              textTransform: 'uppercase',
              letterSpacing: '0.5px'
            }}>
              游늸 Munic칤pios Analisados
            </div>
            <div style={{
              fontSize: '36px',
              fontWeight: '700',
              color: '#ffffff',
              lineHeight: '1',
              marginBottom: '8px'
            }}>
              {filteredAndSortedMunicipalities.length}
            </div>
            <div style={{
              fontSize: '12px',
              color: '#e9d5ff',
              display: 'flex',
              alignItems: 'center',
              gap: '6px'
            }}>
              <span>游딬勇</span>
              <span>{stats.stateRanking.length} estados diferentes</span>
            </div>
          </div>
        </div>

        {/* Ranking de Estados */}
        <div style={{
          backgroundColor: 'rgba(255, 255, 255, 0.08)',
          borderRadius: '12px',
          padding: '20px',
          border: '1px solid rgba(255, 255, 255, 0.12)',
          backdropFilter: 'blur(10px)'
        }}>
          <div style={{
            fontSize: '13px',
            fontWeight: '700',
            color: '#ffffff',
            marginBottom: '16px',
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}>
            <span>游끥</span>
            <span>Top 5 Estados com Maior Subexecu칞칚o</span>
          </div>
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
            gap: '12px'
          }}>
            {stats.stateRanking.map((state, index) => (
              <div key={state.state} style={{
                backgroundColor: 'rgba(255, 255, 255, 0.05)',
