import React, { useState, useMemo } from 'react';
import { municipalityBudgetData } from './mockData/municipalityData';
import { ComparisonMode } from './types/municipality';
import MunicipalityDetailCard from './components/MunicipalityDetailCard';
import AdvancedFilters from './components/AdvancedFilters';
import MunicipalityCard from './components/MunicipalityCard';
import { formatCurrency } from './utils';

function StrategicDashboard() {
  const [comparisonMode, setComparisonMode] = useState<ComparisonMode>('avg-electoral');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedRegion, setSelectedRegion] = useState('Todos');
  const [sortBy, setSortBy] = useState<'deviation' | 'extra' | 'executed'>('extra');
  const [chartType, setChartType] = useState<'line' | 'bar'>('line');
  const [currentPeriod, setCurrentPeriod] = useState<number>(2024);
  const [comparisonPeriod, setComparisonPeriod] = useState<number>(2020);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 12;

  // Filtragem e ordena√ß√£o
  const filteredAndSortedMunicipalities = useMemo(() => {
    let filtered = municipalityBudgetData;

    // Filtro de busca
    if (searchTerm) {
      filtered = filtered.filter(m =>
        m.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        m.state.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Filtro de regi√£o
    if (selectedRegion !== 'Todos') {
      filtered = filtered.filter(m => m.region === selectedRegion);
    }

    // Ordena√ß√£o
    return [...filtered].sort((a, b) => {
      const getComparison = (m: typeof municipalityBudgetData[0]) => {
        switch (comparisonMode) {
          case 'avg-electoral': return m.comparison.avgElectoral;
          case 'last-electoral': return m.comparison.lastElectoral;
          case 'avg-all': return m.comparison.avgAll;
          case 'previous-year': return m.comparison.previousYear;
        }
      };

      const compA = getComparison(a);
      const compB = getComparison(b);

      if (sortBy === 'deviation') {
        return compA.percentageDeviation - compB.percentageDeviation;
      } else if (sortBy === 'extra') {
        const extraA = compA.referenceAmount - compA.executedAmount;
        const extraB = compB.referenceAmount - compB.executedAmount;
        return extraB - extraA;
      } else {
        return compB.executedAmount - compA.executedAmount;
      }
    });
  }, [searchTerm, selectedRegion, sortBy, comparisonMode]);

  // Top 3 para destaque
  const top3 = filteredAndSortedMunicipalities.slice(0, 3);

  // Pagina√ß√£o
  const totalPages = Math.ceil(filteredAndSortedMunicipalities.length / itemsPerPage);
  const paginatedMunicipalities = filteredAndSortedMunicipalities.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  // Estat√≠sticas gerais detalhadas
  const stats = useMemo(() => {
    const getComparison = (mun: typeof filteredAndSortedMunicipalities[0]) => {
      switch (comparisonMode) {
        case 'avg-electoral': return mun.comparison.avgElectoral;
        case 'last-electoral': return mun.comparison.lastElectoral;
        case 'avg-all': return mun.comparison.avgAll;
        case 'previous-year': return mun.comparison.previousYear;
      }
    };

    const totalExtra = filteredAndSortedMunicipalities.reduce((sum, m) => {
      const comp = getComparison(m);
      return sum + (comp.referenceAmount - comp.executedAmount);
    }, 0);

    const totalBudget = filteredAndSortedMunicipalities.reduce((sum, m) => sum + m.totalBudget, 0);
    const totalExecuted = filteredAndSortedMunicipalities.reduce((sum, m) => sum + m.executed, 0);

    const avgDeviation = filteredAndSortedMunicipalities.reduce((sum, m) => {
      return sum + Math.abs(getComparison(m).percentageDeviation);
    }, 0) / (filteredAndSortedMunicipalities.length || 1);

    // Contar por severidade
    const critical = filteredAndSortedMunicipalities.filter(m => getComparison(m).severity === 'critical').length;
    const high = filteredAndSortedMunicipalities.filter(m => getComparison(m).severity === 'high').length;

    // Ranking por estado
    const byState = filteredAndSortedMunicipalities.reduce((acc, m) => {
      if (!acc[m.state]) {
        acc[m.state] = { count: 0, totalExtra: 0 };
      }
      acc[m.state].count++;
      acc[m.state].totalExtra += (getComparison(m).referenceAmount - getComparison(m).executedAmount);
      return acc;
    }, {} as Record<string, { count: number; totalExtra: number }>);

    const stateRanking = Object.entries(byState)
      .map(([state, data]) => ({
        state,
        count: data.count,
        totalExtra: data.totalExtra
      }))
      .sort((a, b) => b.totalExtra - a.totalExtra)
      .slice(0, 5);

    return {
      totalExtra,
      totalBudget,
      totalExecuted,
      avgDeviation,
      critical,
      high,
      stateRanking,
      executionRate: (totalExecuted / totalBudget) * 100
    };
  }, [filteredAndSortedMunicipalities, comparisonMode]);

  return (
    <div style={{
      minHeight: '100vh',
      backgroundColor: '#f9f4ff',
      padding: '32px',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif'
    }}>
      {/* Hero Section - Resumo Brasil */}
      <div style={{
        backgroundColor: '#2e3138',
        borderRadius: '16px',
        padding: '40px',
        marginBottom: '32px',
        boxShadow: '0 4px 16px rgba(0, 0, 0, 0.15)',
        background: 'linear-gradient(135deg, #2e3138 0%, #3f127d 100%)'
      }}>
        <div style={{ textAlign: 'center', marginBottom: '24px' }}>
          <div style={{
            fontSize: '14px',
            fontWeight: '600',
            color: '#d1b3ff',
            textTransform: 'uppercase',
            letterSpacing: '1px',
            marginBottom: '8px'
          }}>
            Vis√£o Estrat√©gica Nacional
          </div>
          <h1 style={{
            fontSize: '36px',
            fontWeight: '700',
            color: '#ffffff',
            margin: 0,
            lineHeight: '1.2'
          }}>
            üéØ Painel Estrat√©gico de Gest√£o Or√ßament√°ria
          </h1>
          <p style={{
            fontSize: '15px',
            color: '#ba90fc',
            marginTop: '12px',
            margin: 0
          }}>
            An√°lise consolidada de {filteredAndSortedMunicipalities.length} munic√≠pios ¬∑ Identifica√ß√£o de oportunidades e recursos dispon√≠veis
          </p>
        </div>

        {/* Stats Grid - Principal */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(240px, 1fr))',
          gap: '16px',
          marginBottom: '16px'
        }}>
          <div style={{
            backgroundColor: 'rgba(16, 185, 129, 0.15)',
            borderRadius: '12px',
            padding: '20px',
            border: '2px solid rgba(16, 185, 129, 0.3)',
            backdropFilter: 'blur(10px)'
          }}>
            <div style={{
              fontSize: '11px',
              fontWeight: '700',
              color: '#6ee7b7',
              marginBottom: '8px',
              textTransform: 'uppercase',
              letterSpacing: '0.5px'
            }}>
              üí∞ Total Dispon√≠vel (Sobra)
            </div>
            <div style={{
              fontSize: '36px',
              fontWeight: '700',
              color: '#ffffff',
              lineHeight: '1',
              marginBottom: '8px'
            }}>
              {formatCurrency(stats.totalExtra, true)}
            </div>
            <div style={{
              fontSize: '12px',
              color: '#a7f3d0',
              display: 'flex',
              alignItems: 'center',
              gap: '6px'
            }}>
              <span>üìà</span>
              <span>de {formatCurrency(stats.totalBudget, true)} or√ßado</span>
            </div>
          </div>

          <div style={{
            backgroundColor: 'rgba(59, 130, 246, 0.15)',
            borderRadius: '12px',
            padding: '20px',
            border: '2px solid rgba(59, 130, 246, 0.3)',
            backdropFilter: 'blur(10px)'
          }}>
            <div style={{
              fontSize: '11px',
              fontWeight: '700',
              color: '#93c5fd',
              marginBottom: '8px',
              textTransform: 'uppercase',
              letterSpacing: '0.5px'
            }}>
              üìä Taxa de Execu√ß√£o M√©dia
            </div>
            <div style={{
              fontSize: '36px',
              fontWeight: '700',
              color: '#ffffff',
              lineHeight: '1',
              marginBottom: '8px'
            }}>
              {stats.executionRate.toFixed(1)}%
            </div>
            <div style={{
              fontSize: '12px',
              color: '#bfdbfe',
              display: 'flex',
              alignItems: 'center',
              gap: '6px'
            }}>
              <span>üí°</span>
              <span>{formatCurrency(stats.totalExecuted, true)} executado</span>
            </div>
          </div>

          <div style={{
            backgroundColor: 'rgba(239, 68, 68, 0.15)',
            borderRadius: '12px',
            padding: '20px',
            border: '2px solid rgba(239, 68, 68, 0.3)',
            backdropFilter: 'blur(10px)'
          }}>
            <div style={{
              fontSize: '11px',
              fontWeight: '700',
              color: '#fca5a5',
              marginBottom: '8px',
              textTransform: 'uppercase',
              letterSpacing: '0.5px'
            }}>
              üö® Desvio do Padr√£o
            </div>
            <div style={{
              fontSize: '36px',
              fontWeight: '700',
              color: '#ffffff',
              lineHeight: '1',
              marginBottom: '8px'
            }}>
              {stats.avgDeviation.toFixed(1)}%
            </div>
            <div style={{
              fontSize: '12px',
              color: '#fecaca',
              display: 'flex',
              alignItems: 'center',
              gap: '6px'
            }}>
              <span>‚ö†Ô∏è</span>
              <span>{stats.critical} cr√≠ticos, {stats.high} altos</span>
            </div>
          </div>

          <div style={{
            backgroundColor: 'rgba(168, 85, 247, 0.15)',
            borderRadius: '12px',
            padding: '20px',
            border: '2px solid rgba(168, 85, 247, 0.3)',
            backdropFilter: 'blur(10px)'
          }}>
            <div style={{
              fontSize: '11px',
              fontWeight: '700',
              color: '#d8b4fe',
              marginBottom: '8px',
              textTransform: 'uppercase',
              letterSpacing: '0.5px'
            }}>
              üìç Munic√≠pios Analisados
            </div>
            <div style={{
              fontSize: '36px',
              fontWeight: '700',
              color: '#ffffff',
              lineHeight: '1',
              marginBottom: '8px'
            }}>
              {filteredAndSortedMunicipalities.length}
            </div>
            <div style={{
              fontSize: '12px',
              color: '#e9d5ff',
              display: 'flex',
              alignItems: 'center',
              gap: '6px'
            }}>
              <span>üó∫Ô∏è</span>
              <span>{stats.stateRanking.length} estados diferentes</span>
            </div>
          </div>
        </div>

        {/* Ranking de Estados */}
        <div style={{
          backgroundColor: 'rgba(255, 255, 255, 0.08)',
          borderRadius: '12px',
          padding: '20px',
          border: '1px solid rgba(255, 255, 255, 0.12)',
          backdropFilter: 'blur(10px)'
        }}>
          <div style={{
            fontSize: '13px',
            fontWeight: '700',
            color: '#ffffff',
            marginBottom: '16px',
            display: 'flex',
            alignItems: 'center',
            gap: '8px'
          }}>
            <span>üèÜ</span>
            <span>Top 5 Estados com Maior Subexecu√ß√£o</span>
          </div>
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
            gap: '12px'
          }}>
            {stats.stateRanking.map((state, index) => (
              <div key={state.state} style={{
                backgroundColor: 'rgba(255, 255, 255, 0.05)',
                borderRadius: '8px',
                padding: '12px',
                border: '1px solid rgba(255, 255, 255, 0.1)'
              }}>
                <div style={{
                  fontSize: '11px',
                  color: '#d1b3ff',
                  fontWeight: '600',
                  marginBottom: '4px',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '6px'
                }}>
                  <span>{index === 0 ? 'ü•á' : index === 1 ? 'ü•à' : index === 2 ? 'ü•â' : `${index + 1}¬∫`}</span>
                  <span>{state.state}</span>
                </div>
                <div style={{
                  fontSize: '18px',
                  fontWeight: '700',
                  color: '#ffffff',
                  marginBottom: '2px'
                }}>
                  {formatCurrency(state.totalExtra, true)}
                </div>
                <div style={{
                  fontSize: '10px',
                  color: '#ba90fc'
                }}>
                  {state.count} munic√≠pio{state.count > 1 ? 's' : ''}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Top 3 Oportunidades */}
      <div style={{ marginBottom: '32px' }}>
        <h2 style={{
          fontSize: '24px',
          fontWeight: '700',
          color: '#2e3138',
          marginBottom: '20px',
          display: 'flex',
          alignItems: 'center',
          gap: '12px'
        }}>
          <span>üèÜ</span>
          <span>Top 3 Maiores Oportunidades (Recursos Dispon√≠veis)</span>
        </h2>
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))',
          gap: '24px'
        }}>
          {top3.map((mun, index) => (
            <MunicipalityCard
              key={mun.id}
              municipality={mun}
              comparisonMode={comparisonMode}
              rank={index + 1}
            />
          ))}
        </div>
      </div>

      {/* Filtros Avan√ßados */}
      <AdvancedFilters
        searchTerm={searchTerm}
        onSearchChange={setSearchTerm}
        comparisonMode={comparisonMode}
        onComparisonModeChange={setComparisonMode}
        selectedRegion={selectedRegion}
        onRegionChange={setSelectedRegion}
        sortBy={sortBy}
        onSortByChange={setSortBy}
        chartType={chartType}
        onChartTypeChange={setChartType}
        currentPeriod={currentPeriod}
        onCurrentPeriodChange={setCurrentPeriod}
        comparisonPeriod={comparisonPeriod}
        onComparisonPeriodChange={setComparisonPeriod}
      />

      {/* Grid de Munic√≠pios com Pagina√ß√£o */}
      <div>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '20px'
        }}>
          <h2 style={{
            fontSize: '20px',
            fontWeight: '700',
            color: '#2e3138',
            margin: 0
          }}>
            üèôÔ∏è Munic√≠pios ({filteredAndSortedMunicipalities.length})
          </h2>
          <div style={{
            fontSize: '14px',
            color: '#6b7280',
            fontWeight: '600'
          }}>
            P√°gina {currentPage} de {totalPages}
          </div>
        </div>

        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(320px, 1fr))',
          gap: '20px',
          marginBottom: '24px'
        }}>
          {paginatedMunicipalities.map(municipality => (
            <MunicipalityDetailCard
              key={municipality.id}
              municipality={municipality}
              mode={comparisonMode}
              onSelect={() => {}}
            />
          ))}
        </div>

        {/* Pagina√ß√£o */}
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          gap: '8px',
          marginTop: '32px'
        }}>
          <button
            onClick={() => setCurrentPage(p => Math.max(1, p - 1))}
            disabled={currentPage === 1}
            style={{
              padding: '10px 20px',
              fontSize: '14px',
              fontWeight: '600',
              border: '2px solid #e1cdfe',
              borderRadius: '8px',
              backgroundColor: currentPage === 1 ? '#f3f4f6' : '#ffffff',
              color: currentPage === 1 ? '#9ca3af' : '#9755fe',
              cursor: currentPage === 1 ? 'not-allowed' : 'pointer',
              transition: 'all 0.2s'
            }}
          >
            ‚Üê Anterior
          </button>

          <div style={{
            display: 'flex',
            gap: '6px'
          }}>
            {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
              let pageNum;
              if (totalPages <= 5) {
                pageNum = i + 1;
              } else if (currentPage <= 3) {
                pageNum = i + 1;
              } else if (currentPage >= totalPages - 2) {
                pageNum = totalPages - 4 + i;
              } else {
                pageNum = currentPage - 2 + i;
              }

              return (
                <button
                  key={pageNum}
                  onClick={() => setCurrentPage(pageNum)}
                  style={{
                    padding: '10px 16px',
                    fontSize: '14px',
                    fontWeight: '600',
                    border: '2px solid',
                    borderColor: currentPage === pageNum ? '#9755fe' : '#e1cdfe',
                    borderRadius: '8px',
                    backgroundColor: currentPage === pageNum ? '#9755fe' : '#ffffff',
                    color: currentPage === pageNum ? '#ffffff' : '#6b7280',
                    cursor: 'pointer',
                    transition: 'all 0.2s',
                    minWidth: '44px'
                  }}
                >
                  {pageNum}
                </button>
              );
            })}
          </div>

          <button
            onClick={() => setCurrentPage(p => Math.min(totalPages, p + 1))}
            disabled={currentPage === totalPages}
            style={{
              padding: '10px 20px',
              fontSize: '14px',
              fontWeight: '600',
              border: '2px solid #e1cdfe',
              borderRadius: '8px',
              backgroundColor: currentPage === totalPages ? '#f3f4f6' : '#ffffff',
              color: currentPage === totalPages ? '#9ca3af' : '#9755fe',
              cursor: currentPage === totalPages ? 'not-allowed' : 'pointer',
              transition: 'all 0.2s'
            }}
          >
            Pr√≥ximo ‚Üí
          </button>
        </div>
      </div>

      {/* Footer */}
      <div style={{
        marginTop: '48px',
        padding: '16px',
        textAlign: 'center',
        color: '#6b7280',
        fontSize: '13px'
      }}>
        <p style={{ margin: 0 }}>
          Painel Estrat√©gico de Gest√£o Or√ßament√°ria ¬∑ Vis√£o Consolidada Nacional
        </p>
      </div>
    </div>
  );
}

export default StrategicDashboard;
